import { useEffect, useRef } from "react";

export default function Orb({ state }) {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    let frame = 0;

    const resize = () => {
      canvas.width = 300;
      canvas.height = 300;
    };

    resize();

    const draw = () => {
      frame++;
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      const cx = canvas.width / 2;
      const cy = canvas.height / 2;

      let pulse =
        state === "listening"
          ? 20
          : state === "speaking"
          ? 30
          : 10;

      const radius = 80 + Math.sin(frame * 0.05) * pulse;

      const gradient = ctx.createRadialGradient(
        cx,
        cy,
        radius * 0.2,
        cx,
        cy,
        radius
      );

      gradient.addColorStop(0, "#8ab4ff");
      gradient.addColorStop(0.5, "#6f7bff");
      gradient.addColorStop(1, "rgba(111,123,255,0.1)");

      ctx.beginPath();
      ctx.arc(cx, cy, radius, 0, Math.PI * 2);
      ctx.fillStyle = gradient;
      ctx.fill();

      requestAnimationFrame(draw);
    };

    draw();
  }, [state]);

  return <canvas ref={canvasRef} className="orb-canvas" />;
}
