import React, { useState } from "react";
import Orb from "./Orb";
import "./App.css";

function App() {
  const [listening, setListening] = useState(false);
  const [speaking, setSpeaking] = useState(false);
  const [aiText, setAiText] = useState("");

  // ---------------- SPEAK (Text â†’ Voice) ----------------
  const speak = (text) => {
  // Stop if already speaking
  if (window.speechSynthesis.speaking) {
    window.speechSynthesis.cancel();
    setSpeaking(false);
    return;
  }

  const utterance = new SpeechSynthesisUtterance(text);

  utterance.lang = "en-US";
  utterance.rate = 0.92;   // softer, calmer
  utterance.pitch = 1.1;   // gentle warmth
  utterance.volume = 1;

  const voices = window.speechSynthesis.getVoices();

  // ðŸŽ¤ Prefer Microsoft Zira (Windows warm female)
  const zira = voices.find(
    (v) =>
      v.name.toLowerCase().includes("zira") ||
      v.name.toLowerCase().includes("female")
  );

  if (zira) {
    utterance.voice = zira;
  }

  setSpeaking(true);

  utterance.onend = () => {
    setSpeaking(false);
  };

  window.speechSynthesis.speak(utterance);
};


  // ---------------- LISTEN (Voice â†’ Text) ----------------
  const startListening = () => {
    // Stop speaking if active
    if (window.speechSynthesis.speaking) {
      window.speechSynthesis.cancel();
      setSpeaking(false);
    }

    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Speech Recognition not supported in this browser");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.continuous = false;

    setListening(true);
    recognition.start();

    recognition.onresult = async (event) => {
      const userText = event.results[0][0].transcript;
      setListening(false);

      try {
        const res = await fetch("http://127.0.0.1:8000/chat", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ message: userText }),
        });

        const data = await res.json();
        setAiText(data.response);
        speak(data.response);
      } catch (err) {
        console.error(err);
      }
    };

    recognition.onerror = () => {
      setListening(false);
    };
  };

  // ---------------- BUTTON LOGIC ----------------
  const handleButtonClick = () => {
    if (speaking) {
      // Stop speaking
      window.speechSynthesis.cancel();
      setSpeaking(false);
      return;
    }

    if (!listening) {
      startListening();
    }
  };

  // ---------------- UI ----------------
  return (
    <div className="App">
      {/* AI Orb */}
      <Orb listening={listening} speaking={speaking} />

      {/* Assistant Name */}
      <h1 className={`alice-title ${speaking ? "speaking" : ""}`}>
    {"Alice".split("").map((char, i) => (
    <span
      key={i}
      className="alice-letter"
      style={{ animationDelay: `${i * 0.12}s` }}>
      {char}
    </span>
    ))}
      </h1>

      {/* Mic Button */}
      <button className="mic-button" onClick={handleButtonClick}>
        {listening
          ? "Listeningâ€¦"
          : speaking
          ? "Speakingâ€¦"
          : "Tap to Speak"}
      </button>
    </div>
  );
}

export default App;
