import { useEffect, useRef } from "react";

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

export default function Orb({ state, onCommand }) {
  const canvasRef = useRef(null);

  // ðŸŽ¨ Animation
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    let frame = 0;

    canvas.width = 300;
    canvas.height = 300;

    const draw = () => {
      frame++;
      ctx.clearRect(0, 0, 300, 300);

      const cx = 150;
      const cy = 150;

      let pulse =
        state === "listening" ? 25 : state === "speaking" ? 35 : 12;

      const radius = 80 + Math.sin(frame * 0.05) * pulse;

      const gradient = ctx.createRadialGradient(
        cx,
        cy,
        radius * 0.2,
        cx,
        cy,
        radius
      );

      gradient.addColorStop(0, "#9fb4ff");
      gradient.addColorStop(0.5, "#6f7bff");
      gradient.addColorStop(1, "rgba(111,123,255,0.1)");

      ctx.beginPath();
      ctx.arc(cx, cy, radius, 0, Math.PI * 2);
      ctx.fillStyle = gradient;
      ctx.fill();

      requestAnimationFrame(draw);
    };

    draw();
  }, [state]);

  // ðŸŽ™ï¸ Click = listen
  const startListening = () => {
    if (!SpeechRecognition) {
      alert("Speech recognition not supported");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.start();

    recognition.onresult = (event) => {
      const text = event.results[0][0].transcript;
      onCommand(text);
    };
  };

  return (
    <canvas
      ref={canvasRef}
      className="orb-canvas"
      onClick={startListening}
      title="Tap to speak"
    />
  );
}
