import { useState } from "react";
import Orb from "./Orb";
import "./Orb.css";
import "./App.css";

export default function App() {
  const [orbState, setOrbState] = useState("idle");

  const startListening = () => {
    setOrbState("listening");

    // simulate listening → speaking → idle
    setTimeout(() => {
      setOrbState("speaking");
    }, 4000);

    setTimeout(() => {
      setOrbState("idle");
    }, 9000);
  };

  return (
    <div className="container">
      <Orb state={orbState} />

      <button
        className="mic-button"
        onClick={startListening}
      >
        {orbState === "listening"
          ? "Listening…"
          : orbState === "speaking"
          ? "Speaking…"
          : "Tap to Speak"}
      </button>
    </div>
  );
}
