import { useState } from "react";
import Orb from "./Orb";
import "./Orb.css";
import "./App.css";

export default function App() {
  const [orbState, setOrbState] = useState("idle");

  const startListening = () => {
    setOrbState("listening");

    // simulate listening â†’ speaking â†’ idle
    setTimeout(() => {
      setOrbState("speaking");
    }, 4000);

    setTimeout(() => {
      setOrbState("idle");
    }, 9000);
  };

  <video
  autoPlay
  loop
  playsInline
  className="bg-video"
  ref={(video) => {
    if (video) video.volume = 0.15; // ðŸ”‰ 15% volume
  }}
>
  <source src="/bg1.mp4" type="video/mp4" />
</video>


  return (
    <div className="container">
      <Orb state={orbState} />

      <button
        className="mic-button"
        onClick={startListening}
      >
        {orbState === "listening"
          ? "Listeningâ€¦"
          : orbState === "speaking"
          ? "Speakingâ€¦"
          : "Tap to Speak"}
      </button>
    </div>
  );
}
