}, [listening]);


  return (
    <div className="container">
      <Orb state={orbState} />

      <button
        className="mic-button"
        onClick={startListening}
      >
        {orbState === "listening"
          ? "Listening…"
          : orbState === "speaking"
          ? "Speaking…"
          : "Tap to Speak"}
      </button>
    </div>
  );
}
